import React, {Component} from "react";
class Pan extends Component {
	constructor() {
		super();

		this.input_blur = this.input_blur.bind();
	}

	input_blur(e) {
		console.log("innnn input_blur")
		console.log(e.target.id)
		console.log(e.target.value)
		 if (e.target.value.value != "") {
            var val = e.target.value;
            var panRegex = /^([a-zA-Z]{5})(\d{4})([a-zA-Z]{1})$/;
             if (panRegex.test(val) == false) {
                alert("Invalid Pan No");
                e.focus();
                return false;
            }
          else
            {
              alert("Correct Pan No");
              }
        }

	}
	render() {
		return (
		 <div className="form">
         <label htmlFor="Pan">Pan Number</label>
         <input type="text" name="pan" id="pan" MaxLength="10" onBlur={this.input_blur}/>
       </div>
		)
	}
}
export default Pan;
