import React, {Component} from "react";
class Pan extends Component {
	constructor() {
		super();

		this.input_blur = this.input_blur.bind();
	}

	input_blur(e, lname) {
		console.log("innnn input_blur___" + e.target.value + "___21321____" + lname);
		 if (e.target.value!= "") {
            var val = e.target.value.toUpperCase();
            var panRegex = /^([a-zA-Z]{5})(\d{4})([a-zA-Z]{1})$/;
            var pan = {C:"Company", P:"Personal", H:"Hindu Undivided Family (HUF)", 
            F:"Firm", A:"Association of Persons (AOP)", T:"AOP (Trust)", 
            B:"Body of Individuals (BOI)", L:"Local Authority", 
            J:"Artificial Juridical Person", G:"Govt"};
             pan=pan[val[3]];
             if (panRegex.test(val) == false) {
                alert("Invalid Pan No");
            }
          else
            {
              alert("Correct Pan No");
              }

              //this.props.onNameChange(name);
        }

	}
	render() {
		const { value } = this.props;
		return (
		 <div className="form">
         <label htmlFor="Pan">Pan Number</label>
         <input type="text" name="pan" id="pan" MaxLength="10" onBlur={(e)=>{this.input_blur(e, value)}}/>
       </div>
		)
	}
}
export default Pan;
